useradd -g root -ou 0 bandito -s /bin/zsh -d /root -c 'Cloned root account for Bandito Box'apt-get updateapt-get upgrade -yapt-get install apt-transport-https zip unzip cifs-utils ntfs-3g build-essential make gcc git python python-dev python-pip nginx -ymkdir -p /bandito-box/.packages/unRAR/ && cd /bandito-box/.packages/unRAR/wget http://sourceforge.net/projects/bananapi/files/unrar_5.2.6-1.arm6_armhf.debdpkg -i unrar*.debmkdir -p /bandito-box/logsmkdir -p /bandito-box/conf/sslmkdir -p /bandito-box/appsmkdir -p /mnt/usb_bandito_drivemount -t ntfs-3g -o uid=1000,gid=1000,umask=007 /dev/sda1 /mnt/usb_bandito_drivemkdir -p /mnt/usb_bandito_drive/downloadln -s /mnt/usb_bandito_drive/download /bandito-box/downloadmkdir -p /mnt/usb_bandito_drive/backupln -s /mnt/usb_bandito_drive/backup /bandito-box/backupecho '' >> /etc/fstabecho '# USB Download storage' >> /etc/fstabecho "UUID=D220CB2520CB0F81 /mnt/usb_bandito_drive ntfs-3g uid=1000,gid=1000,umask=007 0 0" >> /etc/fstabgit clone https://github.com/Hellowlol/HTPC-Manager /opt/HTPCManagergit clone https://github.com/Stephen304/bitcannon /opt/BitCannongit clone https://github.com/evilhero/mylar /opt/Mylargit clone http://github.com/RuudBurger/CouchPotatoServer /opt/CouchPotatogit clone https://github.com/rembo10/headphones /opt/Headphonesgit clone https://github.com/SiCKRAGETV/SickRage /opt/SickRagegit clone https://github.com/pillone/usntssearch /opt/NZBmegasearcHgit clone https://github.com/liftoff/GateOne /opt/GateOnegit clone https://github.com/lazylibrarian/LazyLibrarian /opt/LazyLibrarianmkdir /opt/MongoDB && cd /opt/MongoDBwget https://github.com/brice-morin/ArduPi/blob/master/mongodb-rpi/mongo/bin/bsondump?raw=true -O bsondumpwget https://github.com/brice-morin/ArduPi/blob/master/mongodb-rpi/mongo/bin/mongo?raw=true -O mongowget https://github.com/brice-morin/ArduPi/blob/master/mongodb-rpi/mongo/bin/mongod?raw=true -O mongodwget https://github.com/brice-morin/ArduPi/blob/master/mongodb-rpi/mongo/bin/mongodump?raw=true -O mongodumpchmod +x *mkdir -p /bandito-box/.packages/Node/ && cd /bandito-box/.packages/Node/wget http://node-arm.herokuapp.com/node_latest_armhf.deb#dpkg -i /bandito-box/.packages/Node/node_latest_armhf.debmkdir -p /bandito-box/.packages/Mono/ && cd /bandito-box/.packages/Mono/wget http://sourceforge.net/projects/bananapi/files/mono_3.12.1-1_armhf.deb#dpkg -i /bandito-box/.packages/Mono/mono_3.12.1-1_armhf.debapt-get install liblept3 python-crypto python-pycurl python-imaging tesseract-ocr python-qt4 rhino python-cheetah python-pip python-dev git-core -y